// SPDX-License-Identifier: MIT

pragma solidity >=0.6.12 < 0.7.5;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract Claim is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    IERC20 private _token;

    struct ClaimInfo {
        address claimToken;
        uint claimAmount;
        bool isClaim;
    }

    struct ReportInfo {
        address token;
        uint256 amount;
    }

    mapping(address => ClaimInfo) public claimInfoByToken;

    constructor(IERC20 token) Ownable() public {
        require(address(token) != address(0), "token is the zero address");
        _token = token;
    }

    function token() public view returns (IERC20) {
        return _token;
    }

    /**
     * @dev upload account claimAmount
     * @param claimToken the token of claim
     * @param claimAmount the amount of update
     */
    function updateClaimAmount(address claimToken, uint256 claimAmount) public onlyOwner {
        // Not is 0x address
        require(claimToken != address(0), 'claim from the zero address');
        // the claimAmount must be gt zero
        require(claimAmount > 0, 'the balance is zero');

        ClaimInfo storage info = claimInfoByToken[claimToken];
        if (info.claimToken != address(0)) {
            info.claimAmount = claimAmount;
            info.isClaim = false;
        } else {
            claimInfoByToken[claimToken] = ClaimInfo(claimToken, claimAmount, false);
        }
    }

    /**
     * @dev upload report to update claim list data
     * @param list upload data array
     */
    function updateClaimList(ReportInfo[] memory list) public onlyOwner {
        require(list.length > 0, "list array is null");
        for (uint i = 0; i < list.length; i++) {
            ReportInfo memory reportInfo = list[i];
            if (reportInfo.amount > 0) {
                _addClaimTokens(reportInfo.token, reportInfo.amount);
            }
        }
    }

    /**
     * @dev add claim list data
     * @param claimToken the token of claim
     * @param claimAmount the amount of update
     */
    function _addClaimTokens(address claimToken, uint256 claimAmount) internal {
        claimAmount = claimAmount * (10**uint256(18));
        ClaimInfo storage info = claimInfoByToken[claimToken];
        if (info.claimToken == address(0)) {
            claimInfoByToken[claimToken] = ClaimInfo(claimToken, claimAmount, false);
        }
    }

    /**
     * @dev admin claim
     * @param claimToken the token of claim
     */
    function claimByAdmin(address claimToken) public onlyOwner {
        _claim(claimToken);
    }

    /**
     * @dev self claim
     */
    function claim() public {
        _claim(msg.sender);
    }

    /**
     * @dev common claim function
     * @param claimToken the token of claim
     */
    function _claim(address claimToken) public {
        // Not is 0x address
        require(claimToken != address(0), 'claim from the zero address');
        ClaimInfo storage info = claimInfoByToken[claimToken];
        require(info.claimToken != address(0), 'No Data');
        require(!info.isClaim, 'Already Claim');
        // the balance must be gt zero
        require(info.claimAmount > 0, 'the claimAmount is zero');

        _token.safeTransfer(claimToken, info.claimAmount);
        info.claimAmount = 0;
        info.isClaim = true;
    }

    function killSelf(address payable recipientToken) public onlyOwner {
        uint256 contractBalance = _token.balanceOf(address(this));
        _token.safeTransfer(recipientToken, contractBalance);

        selfdestruct(recipientToken);
    }
}

